from D_smoothPixel import developpe



def getNumberOfPixel(listt):
    toList = list(listt[2][0].values())
    BetterList = []
    for p in range(len(toList)):
        for i in range(len(toList[p])):
            BetterList.append(toList[p][i])
    return len(developpe(BetterList))







def toList(list):
    newList = str(list[0])+"/"+str(list[2])[5:-1]



    return newList

def reverseList(str):
    newStr = str[:str.index("/")] + ", 'TO REPLACE'" + "{" + str[str.index("/") + 1:] + "}"
    newStr = str[:str.index("/")]+", 'TO REPLACE'"+"{"+ str[str.index("/")+1:]+"}"
    newStr = "["+newStr+"]"
    newStr = newStr[:newStr.index("{")]+", "+newStr[newStr.index("{"):]
    newList = eval(newStr)
    newList[1] = int(getNumberOfPixel(newList)/newList[0])





    return newList

